ValidationRule.errorConditionFormula:
OR (
    AND(
        ISPICKVAL(Payout_Cap_Frequency__c , ''),
        NOT(Payout_Cap_Amount__c <= 0)
    ),
    AND(
        NOT(
            ISPICKVAL(Payout_Cap_Frequency__c , '')
        ),
        OR(
            ISBLANK(Payout_Cap_Amount__c),
            Payout_Cap_Amount__c <= 0
        )
    )
)&& (!$Setup.Bypass__c.Validation__c ||(LOWER($Setup.Bypass__c.SObject__c) <> "all" && LOWER($Setup.Bypass__c.SObject__c) <> "careprogram"))
