CustomField.formula:
IF(
				ISNULL(  Survey_Listener_URL__c ) , 'No Survey URL - Survey will not send',
				IF(
								!ISNULL( Recorded_Date__c ) , 'Responded',
								IF( 
												ISNULL( Date_to_Send__c ) && ISNULL(Date_Time_Sent__c), 'Draft / Not Scheduled', 
												IF( 
																!ISNULL( Date_to_Send__c ) && Date_to_Send__c >= TODAY() && ISNULL( Date_Time_Sent__c ), 'Scheduled',
																IF( 
																				!ISNULL( Date_Time_Sent__c ), 'Sent - Waiting response', 
																				IF( 
																								!ISNULL( Date_to_Send__c ) && Date_to_Send__c < TODAY() && ISNULL( Date_Time_Sent__c ), 'Past Due', 
																								'Draft / Not Scheduled' 
																				)
																)
												)
								)
				)
)
